        name: String,
        schlag: {
          name: String,
          flaeche: {
            groesse: String,
            koordinaten: [],
          },
          geopunkte: [],
        },
      },
      massnahmen: {
        pflanzenschutz: [],
        duengung: [],
        pflege: [],
        bonitur: [],
      },
    },
  });
