      DATE: new Date(parseInt(await (await transTransfer[i]).timeStamp) * 1000).toLocaleString("de-DE")
    }) : index;
  }

  return json;
}

async function getAsset() {
  const url = window.location.href;
  assetipfs = url.split("Qm")[1];

  if (typeof assetipfs == "undefined") {
    assetipfs = "bafybei" + url.split("bafybei")[1];
  } else {
    assetipfs = "Qm" + url.split("Qm")[1];
  }

  if (assetipfs === "bafybeiundefined") {
    window.location.assign("/#/error");
  } else {
    const json = JSON.stringify(await (await axios.get("https://gateway.pinata.cloud/ipfs/" + assetipfs)).data);
    return json;
  }
}

function timelineGetter(item, x, fromtodate) {
