/* eslint no-unused-vars: 0 */
import { html, svg, repeat, classMap, styleMap, ifDefined, unsafeHTML, scopeTag } from "@ui5/webcomponents-base/dist/renderer/LitRenderer.js";

const block0 = (context, tags, suffix) => html`<${scopeTag("ui5-dialog", tags, suffix)} aria-label="${ifDefined(context._dialogTitle)}" @ui5-after-open=${ifDefined(context._focusRecentlyUsedControl)} @ui5-before-close=${ifDefined(context._restoreConfirmedOnEscape)} ?stretch=${context._isPhone}><div slot="header" class="ui5-vsd-header"><${scopeTag("ui5-bar", tags, suffix)}><div slot="startContent" class="ui5-vsd-start">${ context.showBackButton ? block1(context, tags, suffix) : block2(context, tags, suffix) }</div><${scopeTag("ui5-button", tags, suffix)} slot="endContent" design="Transparent" @click="${context._resetSettings}" ?disabled=${context._disableResetButton} reset-button>${ifDefined(context._resetButtonLabel)}</${scopeTag("ui5-button", tags, suffix)}></${scopeTag("ui5-bar", tags, suffix)}></div><div class="ui5-vsd-content">${ context.isModeSort ? block3(context, tags, suffix) : undefined }${ context.isModeFilter ? block6(context, tags, suffix) : undefined }</div><div slot="footer" class="ui5-vsd-footer"><${scopeTag("ui5-button", tags, suffix)} design="Emphasized" @click="${context._confirmSettings}">${ifDefined(context._okButtonLabel)}</${scopeTag("ui5-button", tags, suffix)}><${scopeTag("ui5-button", tags, suffix)} design="Transparent" @click="${context._cancelSettings}">${ifDefined(context._cancelButtonLabel)}</${scopeTag("ui5-button", tags, suffix)}></div></${scopeTag("ui5-dialog", tags, suffix)}>`;
const block1 = (context, tags, suffix) => html`<${scopeTag("ui5-button", tags, suffix)} design="Transparent" icon="nav-back" class="ui5-vsd-back-button" @click="${context._navigateToFilters}"></${scopeTag("ui5-button", tags, suffix)}><${scopeTag("ui5-label", tags, suffix)}>${ifDefined(context._filterByTitle)}</${scopeTag("ui5-label", tags, suffix)}>`;
const block2 = (context, tags, suffix) => html`<div class="ui5-vsd-bar-title"><${scopeTag("ui5-label", tags, suffix)} class="ui5-vsd-title">${ifDefined(context._dialogTitle)}</${scopeTag("ui5-label", tags, suffix)}><${scopeTag("ui5-segmented-button", tags, suffix)} @ui5-selection-change="${ifDefined(context._handleModeChange)}"><${scopeTag("ui5-segmented-button-item", tags, suffix)} ?pressed="${context.isModeSort}" ?focused="${context.isModeSort}" icon="sort" mode="Sort"></${scopeTag("ui5-segmented-button-item", tags, suffix)}><${scopeTag("ui5-segmented-button-item", tags, suffix)} ?pressed="${context.isModeFilter}" ?focused="${context.isModeFilter}" icon="filter" mode="Filter"></${scopeTag("ui5-segmented-button-item", tags, suffix)}></${scopeTag("ui5-segmented-button", tags, suffix)}></div>`;
const block3 = (context, tags, suffix) => html`<div class="ui5-vsd-sort"><${scopeTag("ui5-list", tags, suffix)} mode="SingleSelectBegin" @ui5-item-click="${ifDefined(context._onSortOrderChange)}" sort-order><${scopeTag("ui5-li-groupheader", tags, suffix)}>${ifDefined(context._sortOrderLabel)}</${scopeTag("ui5-li-groupheader", tags, suffix)}>${ repeat(context._currentSettings.sortOrder, (item, index) => item._id || index, (item, index) => block4(item, index, context, tags, suffix)) }</${scopeTag("ui5-list", tags, suffix)}><${scopeTag("ui5-list", tags, suffix)} mode="SingleSelectBegin" @ui5-item-click=${ifDefined(context._onSortByChange)} sort-by><${scopeTag("ui5-li-groupheader", tags, suffix)}>${ifDefined(context._sortByLabel)}</${scopeTag("ui5-li-groupheader", tags, suffix)}>${ repeat(context._currentSettings.sortBy, (item, index) => item._id || index, (item, index) => block5(item, index, context, tags, suffix)) }</${scopeTag("ui5-list", tags, suffix)}></div>`;
const block4 = (item, index, context, tags, suffix) => html`<${scopeTag("ui5-li", tags, suffix)} ?selected="${item.selected}">${ifDefined(item.text)}</${scopeTag("ui5-li", tags, suffix)}>`;
const block5 = (item, index, context, tags, suffix) => html`<${scopeTag("ui5-li", tags, suffix)} ?selected="${item.selected}">${ifDefined(item.text)}</${scopeTag("ui5-li", tags, suffix)}>`;
const block6 = (context, tags, suffix) => html`${ context._filterStepTwo ? block7(context, tags, suffix) : block11(context, tags, suffix) }`;
const block7 = (context, tags, suffix) => html`<${scopeTag("ui5-list", tags, suffix)} mode="MultiSelect" @ui5-item-click="${ifDefined(context._handleFilterValueItemClick)}">${ repeat(context._currentSettings.filters, (item, index) => item._id || index, (item, index) => block8(item, index, context, tags, suffix)) }</${scopeTag("ui5-list", tags, suffix)}>`;
const block8 = (item, index, context, tags, suffix) => html`${ item.selected ? block9(item, index, context, tags, suffix) : undefined }`;
const block9 = (item, index, context, tags, suffix) => html`${ repeat(item.filterOptions, (item, index) => item._id || index, (item, index) => block10(item, index, context, tags, suffix)) }`;
const block10 = (item, index, context, tags, suffix) => html`<${scopeTag("ui5-li", tags, suffix)} ?selected=${item.selected}>${ifDefined(item.text)}</${scopeTag("ui5-li", tags, suffix)}>`;
const block11 = (context, tags, suffix) => html`<${scopeTag("ui5-list", tags, suffix)} @ui5-item-click="${ifDefined(context._changeCurrentFilter)}"><${scopeTag("ui5-li-groupheader", tags, suffix)}>${ifDefined(context._filterByLabel)}</${scopeTag("ui5-li-groupheader", tags, suffix)}><slot name="filterItems"></slot></${scopeTag("ui5-list", tags, suffix)}>`;


export default block0;
