			type: Boolean,
		},

		/**
		 * An object of strings that defines several additional accessibility texts
		 * for even further customization.
		 *
		 * It supports the following fields:
		 * - <code>profileButtonTitle</code>: defines the tooltip for the profile button
		 * - <code>logoTitle</code>: defines the tooltip for the logo
		 *
		 * @type {object}
		 * @public
		 * @since 1.1.0
		 */
		 accessibilityTexts: {
			type: Object,
		},

		/**
		 * @private
		 */
		breakpointSize: {
			type: String,
		},

		/**
		 * @private
		 */
		showSearchField: {
			type: Boolean,
		},

		/**
		 * @private
		 */
		coPilotActive: {
			type: Boolean,
		},

		/**
		 * @private
		 */
		withLogo: {
			type: Boolean,
		},

		_itemsInfo: {
			type: Object,
		},

		_header: {
			type: Object,
		},

		_menuPopoverItems: {
			type: String,
			multiple: true,
		},
		_menuPopoverExpanded: {
			type: Boolean,
			noAttribute: true,
		},
		_overflowPopoverExpanded: {
			type: Boolean,
			noAttribute: true,
		},
		_fullWidthSearch: {
			type: Boolean,
			noAttribute: true,
		},
	},
	managedSlots: true,
	slots: /** @lends sap.ui.webcomponents.fiori.ShellBar.prototype */ {
		/**
